import React from "react";
import "./Card.css"

const Card = (props)=>{

    return (
        <div className="ctn-card">
            <div className="icon-card">
                <img src={props.img}/>
            </div>
            <span className="card-title yellow-text">
                {props.title}
            </span>
            <span className="body-text">
                {props.bodyText}
            </span>
        </div>
    )

}

export default Card;
