import React from "react";
import "./SpanText.css"

const SpanText = (props)=>{

    return (
        <div className={props.right?"ctn-span-text right":"ctn-span-text"}>

            {
                props.right?
                <>
                    <div className="span-body">
                        <div className="span-title yellow-text end-flex">
                            {props.title}
                        </div>
                        <div className="span-content end-flex">
                            {props.bodyText}
                        </div>
                    </div>


                    <div className="span-icon">
                        <img src={props.img}/>
                    </div>
                </>
                :
                <>
                    <div className="span-icon">
                        <img src={props.img}/>
                    </div>
                    <div className="span-body">
                        <div className="span-title yellow-text">
                            {props.title}
                        </div>
                        <div className="span-content">
                            {props.bodyText}
                        </div>
                    </div>
                </>
            }
        </div>
    )
}

export default SpanText;
