import React, { useEffect, useState } from "react";
import infosMembros from './membros.json'
import "./Footer.css";

const Footer = (props)=>{
    const [membros1,setMembros1] = useState([]);
    const [membros2,setMembros2] = useState([]);
    useEffect(()=>{

        setMembros1(infosMembros.slice(0,infosMembros.length/2));
        setMembros2(infosMembros.slice(infosMembros.length/2,infosMembros.length));

    },[])

    return (
        <div className="ctn-footer">
            <div className="footer-content">
                <div className="logo-footer">
                    <img src="NEXUS.svg"/>
                </div>
                <div className="membros-footer">
                    Membros
                    <div className="content-membros-1">
                        {membros1.map(membro=> <span>{membro.name}</span>)}
                    </div>

                </div>
            </div>

        </div>
    )
}

export default Footer;
