import React from  'react'
import Navbar from '../../components/navbar/Navbar';
import { useGlobalState } from '../../utils/reactGlobalState';
import "./Home.css"
import { slogan,description } from './texts';
const Home = (props)=>{
    const [click,setClick] = useGlobalState("click");
    //Quando clicar no background fecha o menu se ele tiver aberto
    const handleBackGroundClick = ()=>{
        if(click){
            setClick(false);
        }
    }
    return(
        <div id="home" onClick={handleBackGroundClick} className='ctn-home'>
            <Navbar/>
            <div className='home-content'>
                <div className='text-content'>
                    <div id="slogan">
                        Não se surpreenda com <span className='yellow-text'>atrasos</span>, conheça a NEX<span className='yellow-text'>US</span>
                    </div>
                    <div id="description" className='left-100'>
                        {description}
                    </div>
                </div>
                <div className='app-preview'>
                    <img src='app.svg'/>
                </div>
            </div>
        </div>
    )
}

export default Home;
